blockValues.textContent:
=Offices:
{{ $json.output.offices.map(item => `${item.address}, ${item.city}`).join('\n') }}

Year Founded:
{{ $json.output.year_founded }}

Founders:
{{ $json.output.founders.map(item => `${item.name} (${item.linkedIn})`).join('\n') }}

CEO:
{{ $json.output.ceo.map(item => `${item.name} (${item.linkedIn})`).join('\n') }}

Employees:
{{ $json.output.employees.map(item => `${item.name} - ${item.role}, (${item.linkedIn})`).join('\n') }}

Open Roles:
{{ $json.output.open_jobs.map(item => `${item.role} (${item.published}), ${item.description}`).join('\n') }}

=Money Raised:
{{ $json.output.money_raised || 'unknown' }}

Funding Status:
{{ $json.output.funding_status }}

YoY Customer Growth:
{{ $json.output.yoy_customer_growth || 'unknown' }}

Annual Revenue:
{{ $json.output.annual_revenue || 'unknown' }}

YoY Revenue Growth:
{{ $json.output.yoy_revenue_growth || 'unknown' }}

Investors:
{{ $json.output.investors.map(item => `${item.name}, ${item.description} (${item.linkedIn})`).join('\n') }}

Customers:
{{ $json.output.customers.map(item => `${item.name} (${item.url})`).join('\n') }}

=Pricing Plans:
{{ $json.output.pricing_plans.map(item =>
`${item.name} - ${item.price} (${item.tier})
* ${item.description}`
).join('\n\n') }}

Factors that Impact Price:
{{ $json.output.factors_that_impact_price.map(item => `${item.factor} - ${item.description}`).join('\n') }}

Current Discounts and/or Promotions:
{{ $json.output.discounts_promotions.length ? $json.output.discounts_promotions.map(item =>
  `${item.offer} (${item.start} - ${item.end})
* ${item.description}`
).join('\n\n') : '* None Found' }}

Custom Plans:
{{ $json.output.custom_plans.is_available ? (
  `${$json.output.custom_plans.applicable_for} - ${$json.output.custom_plans.price}
* ${$json.output.custom_plans.description}`
) : 'Not applicable' }}

Free Trials:
{{ $json.output.free_trial.is_available ? (
  `${$json.output.free_trial.applicable_for} - ${$json.output.free_trial.price}
* ${$json.output.free_trial.description}`
) : 'Not applicable' }}

Freemium Version:
{{ $json.output.freemium_version.is_available ? (
  `${$json.output.freemium_version.applicable_for} - ${$json.output.freemium_version.price}
* ${$json.output.freemium_version.description}`
) : 'Not applicable' }}

Complimentary Tools:
{{ $json.output.complementary_tools.map(item =>
  `${item.name} - ${item.price}
* ${item.description}`
).join('\n\n') }}

=Number of Reviews: {{ $json.output.number_of_reviews }}
Positive Mentions (%): {{ $json.output['positive_mentions_%'] }} 
Negative Mentions (%): {{ $json.output['negative_mentions_%'] }} 

Top Pros:
{{ $json.output.top_pros.length ? $json.output.top_pros.map(item => `* ${item}`).join('\n'): '* None Found' }}

Top Cons:
{{ $json.output.top_cons.length ? $json.output.top_cons.map(item => `* ${item}`).join('\n') : '* None Found' }} 

Top Countries:
{{ $json.output.top_countries.length ? $json.output.top_countries.map(item => `* ${item}`).join('\n') : '* None Found' }}

Top Social Media Platforms:
{{ $json.output.top_social_media_platforms.length ? $json.output.top_social_media_platforms.map(item => `* ${item}`).join('\n') : '* None Found' }}

