// https://community.n8n.io/t/iterating-over-email-attachments/13588/3
let results = [];

for (const item of $input.all()) {
  for (key of Object.keys(item.binary)) {
        results.push({
            json: {},
            binary: {
                data: item.binary[key],
            }
        });
    }
}

return results;
